package com.masaicalender.controller;import com.masaicalender.exceptions.EventNotFoundException;import com.masaicalender.exceptions.LoginException;import com.masaicalender.module.Event;import com.masaicalender.services.Implementation.ServicesImplementations;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/masaicalender")public class EventControlHandler {    @Autowired    private ServicesImplementations service;    @PostMapping("/event")    public ResponseEntity<String> eventCreationHandler(@RequestBody Event event,@RequestParam("emailId") String emailId) throws LoginException {           String msg = service.createEvent(event, emailId);           return new ResponseEntity<>(msg, HttpStatus.CREATED);    }    @PutMapping("/event/{id}")    public ResponseEntity<String> eventUpdateHandler(@RequestBody Event event,@RequestParam("emailId")                                                    String emailId, @PathVariable("id") Integer eventId)                                                     throws EventNotFoundException, LoginException {       String msg = service.updateEventById(event, eventId, emailId);        return new ResponseEntity<>(msg, HttpStatus.OK);    }    @DeleteMapping("/event/{id}")    public ResponseEntity<String> eventDeleteHandler(@RequestParam("emailId")    String emailId, @PathVariable("id") Integer eventId)            throws EventNotFoundException, LoginException {        String msg = service.deleteEventById( eventId, emailId);        return new ResponseEntity<>(msg, HttpStatus.OK);    }}