package com.masaicalender.dtos;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.validation.constraints.Email;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotEmpty;@Data@NoArgsConstructor@AllArgsConstructorpublic class LoginDto {    @Email(message = "Enter valid emailId")    private String email;    @NotEmpty    @NotBlank(message = "Give a strong password")    private String password;}