package com.masaicalender.dtos;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.Id;import javax.validation.constraints.Email;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotEmpty;@Data@NoArgsConstructor@AllArgsConstructorpublic class UserDto {    @Id    @Email(message = "Enter a valid email")    private String email;    @NotEmpty    @NotBlank    private String password;    @NotBlank(message = "name can not be empty of blank")    @NotEmpty(message = "name can not be empty of blank")    private String firstName;    @NotBlank(message = "name can not be empty of blank")    @NotEmpty(message = "name can not be empty of blank")    private String lastname;    @NotBlank(message = "number can not be empty of blank")    @NotEmpty(message = "number can not be empty of blank")    private String mobileNumber;    private String dateOfBirth;}